+ private val REFRESH_INTERVAL = intPreferencesKey("refresh_interval")
+ private val USER_NAME = stringPreferencesKey("user_name")
+
+ // Theme Mode Functions
+ fun getThemeMode(context: Context): Flow<String> {
+ return context.dataStore.data.map { preferences ->
+ preferences[THEME_MODE] ?: "system" // Default to system theme
+ }
+ }
+ 
+ suspend fun setThemeMode(context: Context, mode: String) {
+ context.dataStore.edit { preferences ->
+ preferences[THEME_MODE] = mode
+ }
+ }
+
+ // First Run Functions
+ fun isFirstRun(context: Context): Flow<Boolean> {
+ return context.dataStore.data.map { preferences ->
+ preferences[IS_FIRST_RUN] ?: true // Default to true
+ }
+ }
+ 
+ suspend fun setFirstRunCompleted(context: Context) {
+ context.dataStore.edit { preferences ->
+ preferences[IS_FIRST_RUN] = false
+ }
+ }
+
+ // Notification Functions
+ fun getNotificationsEnabled(context: Context): Flow<Boolean> {
+ return context.dataStore.data.map { preferences ->
+ preferences[NOTIFICATION_ENABLED] ?: false // Default to disabled
+ }
+ }
+ 
+ suspend fun setNotificationsEnabled(context: Context, enabled: Boolean) {
+ context.dataStore.edit { preferences ->
+ preferences[NOTIFICATION_ENABLED] = enabled
+ }
+ }
+
+ // Refresh Interval Functions
+ fun getRefreshInterval(context: Context): Flow<Int> {
+ return context.dataStore.data.map { preferences ->
+ preferences[REFRESH_INTERVAL] ?: 15 // Default to 15 minutes
+ }
+ }
+ 
+ suspend fun setRefreshInterval(context: Context, minutes: Int) {
+ context.dataStore.edit { preferences ->
+ preferences[REFRESH_INTERVAL] = minutes
+ }
+ }
+
+ // User Name Functions
+ fun getUserName(context: Context): Flow<String> {
+ return context.dataStore.data.map { preferences ->
+ preferences[USER_NAME] ?: ""
+ }
+ }
+ 
+ suspend fun setUserName(context: Context, name: String) {
+ context.dataStore.edit { preferences ->
+ preferences[USER_NAME] = name
+ }
+ }
+
+ // Clear all settings
+ suspend fun clearAllSettings(context: Context) {
+ context.dataStore.edit { preferences ->
+ preferences.clear()
+ }
+ }
