+package berlin.tu.snet.coinconfigurator.data

+import android.content.Context
+
+/**
+ * Singleton manager for app settings.
+ * Provides easy access to application settings throughout the app.
+ */
+object SettingsManager {
+ private lateinit var settings: Settings
+
+ /**
+ * Initializes the settings manager with a context.
+ * Should be called in Application class or early in app lifecycle.
+ */
+ fun init(context: Context) {
+ if (!::settings.isInitialized) {
+ settings = Settings(context.applicationContext)
+ }
+ }
+
+ /**
+ * Gets the settings instance, throws exception if not initialized.
+ * Use isInitialized() to check before calling if unsure.
+ */
+ fun get(): Settings {
+ check(::settings.isInitialized) { "SettingsManager must be initialized with init() before using" }
+ return settings
+ }
+
+ /**
+ * Checks if the settings manager has been initialized.
+ */
+ fun isInitialized(): Boolean {
+ return ::settings.isInitialized
+ }
+}
