+package berlin.tu.snet.coinconfigurator.serialization

+import android.content.Context
+import android.util.Log
+import berlin.tu.snet.coinconfigurator.model.MachineConfig
+import kotlinx.serialization.decodeFromString
+import kotlinx.serialization.encodeToString
+import java.io.File
+import java.io.IOException
+
+/**
+ * Manager for MachineConfig operations
+ * Handles serialization, deserialization, and storage
+ */
+class MachineConfigManager(private val context: Context) {
+ private val tag = "MachineConfigManager"
+ 
+ // File name for storing the machine config
+ private val configFileName = "machine_config.json"
+ 
+ /**
+ * Load MachineConfig from a string
+ */
+ fun loadFromJson(jsonString: String): MachineConfig? {
+ return try {
+ kotlinx.serialization.json.Json.decodeFromString<MachineConfig>(jsonString)
+ } catch (e: Exception) {
+ Log.e(tag, "Error deserializing config: ${e.message}", e)
+ null
+ }
+ }
+ 
+ /**
+ * Convert MachineConfig to JSON string
+ */
+ fun saveToJson(config: MachineConfig): String? {
+ return try {
+ kotlinx.serialization.json.Json.encodeToString<MachineConfig>(config)
+ } catch (e: Exception) {
+ Log.e(tag, "Error serializing config: ${e.message}", e)
+ null
+ }
+ }
+ 
+ /**
+ * Save MachineConfig to internal storage
+ */
+ fun saveToInternalStorage(config: MachineConfig): Boolean {
+ val jsonString = saveToJson(config) ?: return false
+ return try {
+ context.openFileOutput(configFileName, Context.MODE_PRIVATE).use {
+ it.write(jsonString.toByteArray())
+ }
+ true
+ } catch (e: Exception) {
+ Log.e(tag, "Error writing config to storage: ${e.message}", e)
+ false
+ }
+ }
+ 
+ /**
+ * Load MachineConfig from internal storage
+ */
+ fun loadFromInternalStorage(): MachineConfig? {
+ return try {
+ val jsonString = context.openFileInput(configFileName).bufferedReader().use {
+ it.readText()
+ }
+ loadFromJson(jsonString)
+ } catch (e: Exception) {
+ Log.e(tag, "Error reading config from storage: ${e.message}", e)
+ null
+ }
+ }
+ 
+ /**
+ * Load MachineConfig from a raw resource
+ */
+ fun loadFromResource(resourceId: Int): MachineConfig? {
+ return try {
+ val jsonString = context.resources.openRawResource(resourceId).bufferedReader().use {
+ it.readText()
+ }
+ loadFromJson(jsonString)
+ } catch (e: Exception) {
+ Log.e(tag, "Error reading config from resource: ${e.message}", e)
+ null
+ }
+ }
+ 
+ companion object {
+ // Singleton instance
+ @Volatile
+ private var instance: MachineConfigManager? = null
+ 
+ fun getInstance(context: Context): MachineConfigManager {
+ return instance ?: synchronized(this) {
+ instance ?: MachineConfigManager(context.applicationContext).also { instance = it }
+ }
+ }
+ }
