+package berlin.tu.snet.coinconfigurator.util

+import android.content.Context
+import android.util.Log
+import berlin.tu.snet.coinconfigurator.model.MachineConfig
+import berlin.tu.snet.coinconfigurator.model.Parameter
+import berlin.tu.snet.coinconfigurator.model.ParameterContent
+import org.json.JSONArray
+import org.json.JSONObject
+import java.io.File
+
+/**
+ * Parser for MachineConfig objects using Android's JSON utilities.
+ *
+ * This class provides manual parsing/serialization methods as a bridge until
+ * we can properly implement kotlinx.serialization.
+ */
+object MachineConfigParser {
+ private const val TAG = "MachineConfigParser"
+
+ /**
+ * Parse a MachineConfig from JSON string
+ */
+ fun parse(jsonString: String): MachineConfig? {
+ return try {
+ val json = JSONObject(jsonString)
+
+ // Parse all required fields
+ val id = json.getString("id")
+ val name = json.getString("name")
+ val type = json.getString("type")
+ val folderId = json.optString("folderId", "")
+ val parentId = json.getString("parentId")
+ val sharedAs = json.getString("sharedAs")
+ val createdBy = json.optString("createdBy", "")
+ val createdOn = json.getString("createdOn")
+ val shortname = json.getString("shortname")
+ val lastEdited = json.optString("lastEdited", "")
+ val lastEditedBy = json.optString("lastEditedBy", "")
+ val lastEditedOn = json.getString("lastEditedOn")
+ val environmentId = json.getString("environmentId")
+ val shareTimestamp = json.getLong("shareTimestamp")
+ val allowIframeTimestamp = json.getLong("allowIframeTimestamp")
+
+ // Parse array fields
+ val versions = parseStringArray(json.optJSONArray("versions"))
+ val variables = parseStringArray(json.optJSONArray("variables"))
+ val categories = parseStringArray(json.optJSONArray("categories"))
+ val departments = parseStringArray(json.optJSONArray("departments"))
+ val inEditingBy = parseStringArray(json.optJSONArray("inEditingBy"))
+
+ // Parse metadata
+ val metadata = parseStringMap(json.optJSONObject("metadata"))
+
+ // Parse parameters
+ val parameters = parseParameters(json.optJSONObject("parameters"))
+
+ // Create MachineConfig instance
+ MachineConfig(
+ id = id,
+ name = name,
+ type = type,
+ folderId = folderId,
+ metadata = metadata,
+ parentId = parentId,
+ sharedAs = sharedAs,
+ versions = versions,
+ createdBy = createdBy,
+ createdOn = createdOn,
+ shortname = shortname,
+ variables = variables,
+ categories = categories,
+ lastEdited = lastEdited,
+ parameters = parameters,
+ departments = departments,
+ inEditingBy = inEditingBy,
+ lastEditedBy = lastEditedBy,
+ lastEditedOn = lastEditedOn,
+ environmentId = environmentId,
+ shareTimestamp = shareTimestamp,
+ allowIframeTimestamp = allowIframeTimestamp
+ )
+ } catch (e: Exception) {
+ Log.e(TAG, "Error parsing MachineConfig: ${e.message}", e)
+ null
+ }
+ }
+
+ /**
+ * Parse MachineConfig from a file
+ */
+ fun parseFromFile(file: File): MachineConfig? {
+ return try {
+ val jsonString = file.readText()
+ parse(jsonString)
+ } catch (e: Exception) {
+ Log.e(TAG, "Error reading from file: ${e.message}", e)
+ null
+ }
+ }
+
+ /**
+ * Parse MachineConfig from a resource
+ */
+ fun parseFromResource(context: Context, resourceId: Int): MachineConfig? {
+ return try {
+ val jsonString = context.resources.openRawResource(resourceId).bufferedReader().use {
+ it.readText()
+ }
+ parse(jsonString)
+ } catch (e: Exception) {
+ Log.e(TAG, "Error reading from resource: ${e.message}", e)
+ null
+ }
+ }
+
+ /**
+ * Convert MachineConfig to JSON string
+ */
+ fun toJson(config: MachineConfig): String {
+ val json = JSONObject().apply {
+ put("id", config.id)
+ put("name", config.name)
+ put("type", config.type)
+ put("folderId", config.folderId)
+ put("parentId", config.parentId)
+ put("sharedAs", config.sharedAs)
+ put("createdBy", config.createdBy)
+ put("createdOn", config.createdOn)
+ put("shortname", config.shortname)
+ put("lastEdited", config.lastEdited)
+ put("lastEditedBy", config.lastEditedBy)
+ put("lastEditedOn", config.lastEditedOn)
+ put("environmentId", config.environmentId)
+ put("shareTimestamp", config.shareTimestamp)
+ put("allowIframeTimestamp", config.allowIframeTimestamp)
+
+ // Add array items
+ put("versions", JSONArray(config.versions))
+ put("variables", JSONArray(config.variables))
+ put("categories", JSONArray(config.categories))
+ put("departments", JSONArray(config.departments))
+ put("inEditingBy", JSONArray(config.inEditingBy))
+
+ // Add metadata
+ put("metadata", JSONObject(config.metadata))
+
+ // Add parameters
+ put("parameters", parametersToJson(config.parameters))
+ }
+
+ return json.toString(2) // Pretty print with 2-space indentation
+ }
+
+ // Helper methods
+
+ private fun parseStringArray(jsonArray: JSONArray?): List<String> {
+ val result = mutableListOf<String>()
+ if (jsonArray == null) return result
+
+ for (i in 0 until jsonArray.length()) {
+ result.add(jsonArray.getString(i))
+ }
+ return result
+ }
+
+ private fun parseStringMap(jsonObj: JSONObject?): Map<String, String> {
+ val result = mutableMapOf<String, String>()
+ if (jsonObj == null) return result
+
+ val keys = jsonObj.keys()
+ while (keys.hasNext()) {
+ val key = keys.next()
+ result[key] = jsonObj.optString(key, "")
+ }
+ return result
+ }
+
+ private fun parseParameters(jsonObj: JSONObject?): Map<String, Parameter> {
+ val result = mutableMapOf<String, Parameter>()
+ if (jsonObj == null) return result
+
+ val keys = jsonObj.keys()
+ while (keys.hasNext()) {
+ val key = keys.next()
+ val paramObj = jsonObj.getJSONObject(key)
+
+ val id = paramObj.getString("id")
+ val paramKey = paramObj.getString("key")
+ val type = paramObj.getString("type")
+ val parentId = paramObj.getString("parentId")
+ val parentType = paramObj.getString("parentType")
+
+ // Parse content array
+ val contentArray = paramObj.optJSONArray("content")
+ val content = mutableListOf<ParameterContent>()
+
+ if (contentArray != null) {
+ for (i in 0 until contentArray.length()) {
+ val contentObj = contentArray.getJSONObject(i)
+ content.add(
+ ParameterContent(
+ unit = contentObj.optString("unit", ""),
+ value = contentObj.optString("value", ""),
+ language = contentObj.getString("language"),
+ displayName = contentObj.getString("displayName")
+ )
+ )
+ }
+ }
+
+ // Parse nested parameters
+ val nestedParams = parseParameters(paramObj.optJSONObject("parameters"))
+
+ // Parse linked parameters
+ val linkedParams = parseStringArray(paramObj.optJSONArray("linkedParameters"))
+
+ result[key] = Parameter(
+ id = id,
+ key = paramKey,
+ type = type,
+ content = content,
+ parentId = parentId,
+ parameters = nestedParams,
+ parentType = parentType,
+ linkedParameters = linkedParams
+ )
+ }
+
+ return result
+ }
+
+ private fun parametersToJson(parameters: Map<String, Parameter>): JSONObject {
+ val result = JSONObject()
+
+ parameters.forEach { (key, param) ->
+ val paramObj = JSONObject().apply {
+ put("id", param.id)
+ put("key", param.key)
+ put("type", param.type)
+ put("parentId", param.parentId)
+ put("parentType", param.parentType)
+
+ // Add content array
+ val contentArray = JSONArray()
+ param.content.forEach { content ->
+ contentArray.put(JSONObject().apply {
+ put("unit", content.unit)
+ put("value", content.value)
+ put("language", content.language)
+ put("displayName", content.displayName)
+ })
+ }
+ put("content", contentArray)
+
+ // Add nested parameters
+ if (param.parameters.isNotEmpty()) {
+ put("parameters", parametersToJson(param.parameters))
+ }
+
+ // Add linked parameters
+ put("linkedParameters", JSONArray(param.linkedParameters))
+ }
+
+ result.put(key, paramObj)
+ }
+
+ return result
+ }
+}
```

