+ private fun parseMetadataEntries(jsonObj: JSONObject?): Map<String, MetadataEntry> {
+ val result = mutableMapOf<String, MetadataEntry>()
+ if (jsonObj == null) return result
+
+ val keys = jsonObj.keys()
+ while (keys.hasNext()) {
+ val key = keys.next()
+ val metadataObj = jsonObj.getJSONObject(key)
+
+ val id = metadataObj.getString("id")
+ val metadataKey = metadataObj.getString("key")
+ val type = metadataObj.getString("type")
+ val parentId = metadataObj.getString("parentId")
+ val parentType = metadataObj.getString("parentType")
+
+ // Parse content array
+ val contentArray = metadataObj.optJSONArray("content")
+ val content = mutableListOf<ParameterContent>()
+
+ if (contentArray != null) {
+ for (i in 0 until contentArray.length()) {
+ val contentObj = contentArray.getJSONObject(i)
+ content.add(
+ ParameterContent(
+ unit = contentObj.optString("unit", ""),
+ value = contentObj.optString("value", ""),
+ language = contentObj.getString("language"),
+ displayName = contentObj.getString("displayName")
+ )
+ )
+ }
+ }
+
+ // Parse nested parameters
+ val nestedParams = parseParameters(metadataObj.optJSONObject("parameters"))
+
+ // Parse linked parameters
+ val linkedParams = parseStringArray(metadataObj.optJSONArray("linkedParameters"))
+
+ result[key] = MetadataEntry(
+ id = id,
+ key = metadataKey,
+ type = type,
+ content = content,
+ parentId = parentId,
+ parameters = nestedParams,
+ parentType = parentType,
+ linkedParameters = linkedParams
+ )
+ }
+
+ return result
+ }
+
 private fun parseParameters(jsonObj: JSONObject?): Map<String, Parameter> {
 val result = mutableMapOf<String, Parameter>()
@@ -268,3 +536,42 @@
