+
+ private fun metadataEntriesToJson(metadata: Map<String, MetadataEntry>): JSONObject {
+ val result = JSONObject()
+
+ metadata.forEach { (key, entry) ->
+ val entryObj = JSONObject().apply {
+ put("id", entry.id)
+ put("key", entry.key)
+ put("type", entry.type)
+ put("parentId", entry.parentId)
+ put("parentType", entry.parentType)
+
+ // Add content array
+ val contentArray = JSONArray()
+ entry.content.forEach { content ->
+ contentArray.put(JSONObject().apply {
+ put("unit", content.unit)
+ put("value", content.value)
+ put("language", content.language)
+ put("displayName", content.displayName)
+ })
+ }
+ put("content", contentArray)
+
+ // Add nested parameters
+ if (entry.parameters.isNotEmpty()) {
+ put("parameters", parametersToJson(entry.parameters))
+ }
+
+ // Add linked parameters
+ put("linkedParameters", JSONArray(entry.linkedParameters))
+ }
+
+ result.put(key, entryObj)
+ }
+
+ return result
+ }
