+/**
+ * Utility class to load configuration files from assets or resources
+ */
+object ConfigLoader {
+ private const val TAG = "ConfigLoader"
+
+ /**
+ * Load a MachineConfig from assets
+ *
+ * @param context Application context
+ * @param fileName Name of the file in the assets folder
+ * @return Parsed MachineConfig or null if an error occurred
+ */
+ suspend fun loadMachineConfigFromAssets(context: Context, fileName: String): MachineConfig? {
+ return withContext(Dispatchers.IO) {
+ try {
+ val jsonString = context.assets.readAssetFile(fileName)
+ MachineConfigParser.parse(jsonString)
+ } catch (e: Exception) {
+ Log.e(TAG, "Error loading machine config from assets: ${e.message}", e)
+ null
+ }
+ }
+ }
+
+ /**
+ * Load a FullConfig from assets
+ *
+ * @param context Application context
+ * @param fileName Name of the file in the assets folder
+ * @return Parsed FullConfig or null if an error occurred
+ */
+ suspend fun loadFullConfigFromAssets(context: Context, fileName: String): FullConfig? {
+ return withContext(Dispatchers.IO) {
+ try {
+ val jsonString = context.assets.readAssetFile(fileName)
+ MachineConfigParser.parseFullConfig(jsonString)
+ } catch (e: Exception) {
+ Log.e(TAG, "Error loading full config from assets: ${e.message}", e)
+ null
+ }
+ }
+ }
+
+ /**
+ * Load a MachineConfig from a raw resource
+ *
+ * @param context Application context
+ * @param resourceId Resource ID of the JSON file
+ * @return Parsed MachineConfig or null if an error occurred
+ */
+ suspend fun loadMachineConfigFromResource(context: Context, resourceId: Int): MachineConfig? {
+ return withContext(Dispatchers.IO) {
+ MachineConfigParser.parseFromResource(context, resourceId)
+ }
+ }
+
+ /**
+ * Load a FullConfig from a raw resource
+ *
+ * @param context Application context
+ * @param resourceId Resource ID of the JSON file
+ * @return Parsed FullConfig or null if an error occurred
+ */
+ suspend fun loadFullConfigFromResource(context: Context, resourceId: Int): FullConfig? {
+ return withContext(Dispatchers.IO) {
+ MachineConfigParser.parseFullConfigFromResource(context, resourceId)
+ }
+ }
