+package berlin.tu.snet.coinconfigurator.network

+import java.util.concurrent.TimeUnit
+
+/**
+ * Network module that provides Retrofit and API service instances
+ */
+object NetworkModule {
+ private const val BASE_URL = "http://research.snet.tu-berlin.de:3000/api/"
+ private const val TIMEOUT_SECONDS = 30L
+ 
+ /**
+ * Create OkHttpClient with logging
+ */
+ private fun createOkHttpClient(): OkHttpClient {
+ val loggingInterceptor = HttpLoggingInterceptor().apply {
+ level = HttpLoggingInterceptor.Level.BODY
+ }
+ 
+ return OkHttpClient.Builder()
+ .addInterceptor(loggingInterceptor)
+ .connectTimeout(TIMEOUT_SECONDS, TimeUnit.SECONDS)
+ .readTimeout(TIMEOUT_SECONDS, TimeUnit.SECONDS)
+ .writeTimeout(TIMEOUT_SECONDS, TimeUnit.SECONDS)
+ .build()
+ }
+ 
+ /**
+ * Create Gson instance
+ */
+ private fun createGson(): Gson {
+ return GsonBuilder()
+ .setLenient()
+ .create()
+ }
+ 
+ /**
+ * Create Retrofit instance
+ */
+ private fun createRetrofit(): Retrofit {
+ return Retrofit.Builder()
+ .baseUrl(BASE_URL)
+ .client(createOkHttpClient())
+ .addConverterFactory(GsonConverterFactory.create(createGson()))
+ .build()
+ }
+ 
+ /**
+ * Provide ConfigApiService
+ */
+ fun provideConfigApiService(): ConfigApiService {
+ return createRetrofit().create(ConfigApiService::class.java)
+ }
+}
