+/**
+ * Repository for handling configuration data operations
+ */
+class ConfigRepository(
+ private val apiService: ConfigApiService,
+ private val configPreferences: ConfigPreferences
+) {
+ private val TAG = "ConfigRepository"
+
+ /**
+ * Get the full configuration from the server
+ *
+ * @return Flow emitting the FullConfig or null if an error occurred
+ */
+ fun getFullConfig(): Flow<Result<FullConfig>> = flow {
+ try {
+ val spaceId = configPreferences.spaceId.first()
+ val configContainerId = configPreferences.configContainerId.first()
+ val versionId = configPreferences.versionId.first()
+
+ if (spaceId.isEmpty() || configContainerId.isEmpty() || versionId.isEmpty()) {
+ emit(Result.failure(IllegalStateException("Configuration IDs not set")))
+ return@flow
+ }
+
+ val response = apiService.getFullConfig(spaceId, configContainerId, versionId)
+ if (response.isSuccessful) {
+ val fullConfig = response.body()
+ if (fullConfig != null) {
+ emit(Result.success(fullConfig))
+ } else {
+ emit(Result.failure(IllegalStateException("Empty response body")))
+ }
+ } else {
+ emit(Result.failure(RuntimeException("Error: ${response.code()} - ${response.message()}")))
+ }
+ } catch (e: Exception) {
+ Log.e(TAG, "Error fetching configuration", e)
+ emit(Result.failure(e))
+ }
+ }
+
+ /**
+ * Get the first machine config from the full config
+ *
+ * @return Flow emitting the MachineConfig or null if not available
+ */
+ fun getMachineConfig(): Flow<Result<MachineConfig>> = flow {
+ try {
+ getFullConfig().collect { result ->
+ if (result.isSuccess) {
+ val fullConfig = result.getOrNull()
+ if (fullConfig != null && fullConfig.machineConfigs.isNotEmpty()) {
+ emit(Result.success(fullConfig.machineConfigs.first()))
+ } else {
+ emit(Result.failure(IllegalStateException("No machine configurations available")))
+ }
+ } else {
+ emit(
+ Result.failure(
+ result.exceptionOrNull() ?: IllegalStateException("Unknown error")
+ )
+ )
+ }
+ }
+ } catch (e: Exception) {
+ Log.e(TAG, "Error getting machine config", e)
+ emit(Result.failure(e))
+ }
+ }
+
+ /**
+ * Update a parameter in the configuration
+ *
+ * @param parameterId ID of the parameter to update
+ * @param parameter Updated parameter data
+ * @return Flow indicating success or failure
+ */
+ fun updateParameter(parameterId: String, parameter: Parameter): Flow<Result<Unit>> = flow {
+ try {
+ val spaceId = configPreferences.spaceId.first()
+ val configContainerId = configPreferences.configContainerId.first()
+ val versionId = configPreferences.versionId.first()
+
+ if (spaceId.isEmpty() || configContainerId.isEmpty() || versionId.isEmpty()) {
+ emit(Result.failure(IllegalStateException("Configuration IDs not set")))
+ return@flow
+ }
+
+ val response = apiService.updateParameter(
+ spaceId, configContainerId, versionId, parameterId, parameter
+ )
+
+ if (response.isSuccessful) {
+ emit(Result.success(Unit))
+ } else {
+ emit(Result.failure(RuntimeException("Error: ${response.code()} - ${response.message()}")))
+ }
+ } catch (e: Exception) {
+ Log.e(TAG, "Error updating parameter", e)
+ emit(Result.failure(e))
+ }
+ }
