+package berlin.tu.snet.coinconfigurator.viewmodel

+import androidx.lifecycle.ViewModel
+import androidx.lifecycle.viewModelScope
+import berlin.tu.snet.coinconfigurator.model.MachineConfig
+import berlin.tu.snet.coinconfigurator.model.Parameter
+import berlin.tu.snet.coinconfigurator.model.ParameterContent
+import berlin.tu.snet.coinconfigurator.repository.ConfigRepository
+import kotlinx.coroutines.flow.MutableStateFlow
+import kotlinx.coroutines.flow.StateFlow
+import kotlinx.coroutines.flow.asStateFlow
+import kotlinx.coroutines.flow.catch
+import kotlinx.coroutines.launch
+
+/**
+ * ViewModel for handling configuration data and user interactions
+ */
+class ConfigViewModel(private val repository: ConfigRepository) : ViewModel() {
+
+ // State for machine config data
+ private val _machineConfig = MutableStateFlow<MachineConfig?>(null)
+ val machineConfig: StateFlow<MachineConfig?> = _machineConfig.asStateFlow()
+
+ // State for loading indicator
+ private val _isLoading = MutableStateFlow(false)
+ val isLoading: StateFlow<Boolean> = _isLoading.asStateFlow()
+
+ // State for error messages
+ private val _errorMessage = MutableStateFlow<String?>(null)
+ val errorMessage: StateFlow<String?> = _errorMessage.asStateFlow()
+
+ // State for success messages
+ private val _successMessage = MutableStateFlow<String?>(null)
+ val successMessage: StateFlow<String?> = _successMessage.asStateFlow()
+
+ /**
+ * Load the machine configuration data
+ */
+ fun loadMachineConfig() {
+ viewModelScope.launch {
+ _isLoading.value = true
+ _errorMessage.value = null
+
+ repository.getMachineConfig()
+ .catch { e ->
+ _errorMessage.value = "Failed to load configuration: ${e.message}"
+ _isLoading.value = false
+ }
+ .collect { result ->
+ result.onSuccess { config ->
+ _machineConfig.value = config
+ _isLoading.value = false
+ }.onFailure { error ->
+ _errorMessage.value = "Failed to load configuration: ${error.message}"
+ _isLoading.value = false
+ }
+ }
+ }
+ }
+
+ /**
+ * Update a parameter value
+ *
+ * @param parameterId ID of the parameter to update
+ * @param newValue New value for the parameter
+ */
+ fun updateParameterValue(parameterId: String, newValue: String) {
+ val currentConfig = _machineConfig.value ?: return
+
+ // Find the parameter with the given ID
+ val parameter = findParameterById(currentConfig.parameters, parameterId) ?: return
+
+ // Create an updated parameter with the new value
+ val updatedParameter = updateParameterContent(parameter, newValue)
+
+ // Send the update to the server
+ viewModelScope.launch {
+ _isLoading.value = true
+ _errorMessage.value = null
+ _successMessage.value = null
+
+ repository.updateParameter(parameterId, updatedParameter)
+ .catch { e ->
+ _errorMessage.value = "Failed to update parameter: ${e.message}"
+ _isLoading.value = false
+ }
+ .collect { result ->
+ result.onSuccess {
+ _successMessage.value = "Parameter updated successfully"
+ loadMachineConfig() // Reload to get the latest data
+ }.onFailure { error ->
+ _errorMessage.value = "Failed to update parameter: ${error.message}"
+ _isLoading.value = false
+ }
+ }
+ }
+ }
+
+ /**
+ * Find a parameter by ID in the parameter map (recursive search)
+ *
+ * @param parameters Map of parameters to search
+ * @param parameterId ID of the parameter to find
+ * @return The parameter with the given ID or null if not found
+ */
+ private fun findParameterById(
+ parameters: Map<String, Parameter>,
+ parameterId: String
+ ): Parameter? {
+ // Check if the parameter is directly in this map
+ for (parameter in parameters.values) {
+ if (parameter.id == parameterId) {
+ return parameter
+ }
+
+ // Recursively search in nested parameters
+ if (parameter.parameters.isNotEmpty()) {
+ val nestedResult = findParameterById(parameter.parameters, parameterId)
+ if (nestedResult != null) {
+ return nestedResult
+ }
+ }
+ }
+
+ return null
+ }
+
+ /**
+ * Update the parameter content with a new value
+ *
+ * @param parameter The parameter to update
+ * @param newValue The new value for the parameter
+ * @return The updated parameter
+ */
+ private fun updateParameterContent(parameter: Parameter, newValue: String): Parameter {
+ val updatedContent = parameter.content.map { content ->
+ ParameterContent(
+ unit = content.unit,
+ value = newValue, // Update the value
+ language = content.language,
+ displayName = content.displayName
+ )
+ }
+
+ return Parameter(
+ id = parameter.id,
+ key = parameter.key,
+ type = parameter.type,
+ content = updatedContent,
+ parentId = parameter.parentId,
+ parameters = parameter.parameters,
+ parentType = parameter.parentType,
+ linkedParameters = parameter.linkedParameters
+ )
+ }
+
+ /**
+ * Clear error message
+ */
+ fun clearErrorMessage() {
+ _errorMessage.value = null
+ }
+
+ /**
+ * Clear success message
+ */
+ fun clearSuccessMessage() {
+ _successMessage.value = null
+ }
+}
