+import androidx.compose.ui.text.input.KeyboardType
+import androidx.compose.ui.unit.dp
+import berlin.tu.snet.coinconfigurator.model.Parameter
+import berlin.tu.snet.coinconfigurator.model.ParameterContent
+
+/**
+ * Composable for displaying and editing a parameter
+ *
+ * @param parameter The parameter to display
+ * @param onValueChange Callback when the parameter value changes
+ */
+@Composable
+fun ParameterInputField(
+ parameter: Parameter,
+ onValueChange: (String, String) -> Unit
+) {
+ // Get first content element (assuming there's always at least one)
+ val content = parameter.content.firstOrNull() ?: return
+ val displayName = content.displayName.ifEmpty { parameter.key }
+ val value = content.value
+ val unit = content.unit
+
+ Column(
+ modifier = Modifier
+ .fillMaxWidth()
+ .padding(vertical = 8.dp)
+ ) {
+ when {
+ // Boolean type (true/false)
+ value.equals("true", ignoreCase = true) || value.equals("false", ignoreCase = true) -> {
+ BooleanParameterInput(
+ parameter = parameter,
+ content = content,
+ onValueChange = onValueChange
+ )
+ }
+
+ // Numeric type
+ value.toDoubleOrNull() != null || value.isEmpty() -> {
+ NumericParameterInput(
+ parameter = parameter,
+ content = content,
+ onValueChange = onValueChange
+ )
+ }
+
+ // String type (default)
+ else -> {
+ StringParameterInput(
+ parameter = parameter,
+ content = content,
+ onValueChange = onValueChange
+ )
+ }
+ }
+ }
+}
+
+/**
+ * Input field for boolean parameters
+ */
+@Composable
+fun BooleanParameterInput(
+ parameter: Parameter,
+ content: ParameterContent,
+ onValueChange: (String, String) -> Unit
+) {
+ val displayName = content.displayName.ifEmpty { parameter.key }
+ var isChecked by remember { mutableStateOf(content.value.equals("true", ignoreCase = true)) }
+
+ Row(
+ modifier = Modifier.fillMaxWidth(),
+ verticalAlignment = Alignment.CenterVertically
+ ) {
+ Text(
+ text = displayName,
+ style = MaterialTheme.typography.bodyLarge,
+ modifier = Modifier.weight(1f)
+ )
+ Switch(
+ checked = isChecked,
+ onCheckedChange = { checked ->
+ isChecked = checked
+ onValueChange(parameter.id, if (checked) "true" else "false")
+ }
+ )
+ }
+}
+
+/**
+ * Input field for numeric parameters
+ */
+@Composable
+fun NumericParameterInput(
+ parameter: Parameter,
+ content: ParameterContent,
+ onValueChange: (String, String) -> Unit
+) {
+ val displayName = content.displayName.ifEmpty { parameter.key }
+ var value by remember { mutableStateOf(content.value) }
+ val unit = content.unit
+
+ Column(modifier = Modifier.fillMaxWidth()) {
+ Text(
+ text = displayName,
+ style = MaterialTheme.typography.bodyLarge
+ )
+ Spacer(modifier = Modifier.height(4.dp))
+ Row(
+ verticalAlignment = Alignment.CenterVertically,
+ modifier = Modifier.fillMaxWidth()
+ ) {
+ OutlinedTextField(
+ value = value,
+ onValueChange = { newValue ->
+ value = newValue
+ onValueChange(parameter.id, newValue)
+ },
+ modifier = Modifier.weight(1f),
+ keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
+ singleLine = true
+ )
+ if (unit.isNotEmpty()) {
+ Spacer(modifier = Modifier.width(8.dp))
+ Text(
+ text = unit,
+ style = MaterialTheme.typography.bodyMedium
+ )
+ }
+ }
+ }
+}
+
+/**
+ * Input field for string parameters
+ */
+@Composable
+fun StringParameterInput(
+ parameter: Parameter,
+ content: ParameterContent,
+ onValueChange: (String, String) -> Unit
+) {
+ val displayName = content.displayName.ifEmpty { parameter.key }
+ var value by remember { mutableStateOf(content.value) }
+
+ Column(modifier = Modifier.fillMaxWidth()) {
+ Text(
+ text = displayName,
+ style = MaterialTheme.typography.bodyLarge
+ )
+ Spacer(modifier = Modifier.height(4.dp))
+ OutlinedTextField(
+ value = value,
+ onValueChange = { newValue ->
+ value = newValue
+ onValueChange(parameter.id, newValue)
+ },
+ modifier = Modifier.fillMaxWidth(),
+ singleLine = true
+ )
+ }
+}
