+/**
+ * Group parameters by category for better organization
+ *
+ * @param parameters The flat list of parameters
+ * @return A map of category to list of parameters in that category
+ */
+private fun groupParametersByCategory(parameters: List<Parameter>): Map<String, List<Parameter>> {
+ val result = mutableMapOf<String, MutableList<Parameter>>()
+
+ // Predefined categories based on parameter keys
+ val sizeParameters = parameters.filter {
+ it.key == "width" || it.key == "height"
+ }
+ if (sizeParameters.isNotEmpty()) {
+ result["Size"] = sizeParameters.toMutableList()
+ }
+
+ val colorParameters = parameters.filter {
+ it.key == "coin-color" || it.key == "logo-color" ||
+ it.key.contains("color", ignoreCase = true)
+ }
+ if (colorParameters.isNotEmpty()) {
+ result["Color"] = colorParameters.toMutableList()
+ }
+
+ val logoParameters = parameters.filter {
+ it.key == "logo" || it.key.contains("logo", ignoreCase = true)
+ }
+ if (logoParameters.isNotEmpty()) {
+ result["Logo"] = logoParameters.toMutableList()
+ }
+
+ val printingHeadParameters = parameters.filter {
+ it.key.contains("printing-head", ignoreCase = true) ||
+ it.key == "coin-printing-head-no"
+ }
+ if (printingHeadParameters.isNotEmpty()) {
+ result["Printing Heads"] = printingHeadParameters.toMutableList()
+ }
+
+ val coordinateParameters = parameters.filter {
+ it.key == "coordinates" || it.key == "x" || it.key == "y"
+ }
+ if (coordinateParameters.isNotEmpty()) {
+ result["Coordinates"] = coordinateParameters.toMutableList()
+ }
+
+ // Put remaining parameters in Other category
+ val otherParameters = parameters.filter { parameter ->
+ !result.values.flatten().contains(parameter)
+ }
+ if (otherParameters.isNotEmpty()) {
+ result["Other"] = otherParameters.toMutableList()
+ }
+
+ return result
+}
