 /**
+ * Check if a parameter is a color parameter that needs special handling
+ */
+ private fun isColorParameter(parameter: Parameter): Boolean {
+ return parameter.key == "coin-color" || parameter.key == "logo-color" ||
+ parameter.key.contains("color", ignoreCase = true)
+ }
+
+ /**
+ * Update a color parameter, which involves mapping color to printing head number
+ */
+ private fun updateColorParameter(parameter: Parameter, colorValue: String) {
+ viewModelScope.launch {
+ _isLoading.value = true
+
+ try {
+ // Find the associated printing head parameter 
+ val printingHeadParam = findPrintingHeadForColorParameter(parameter)
+
+ if (printingHeadParam != null) {
+ // Get the printing head number for this color from preferences
+ val headNumber = configPreferences.getPrintingHeadForColor(colorValue)
+
+ // Update the color value
+ val updatedColorParam = updateParameterContent(parameter, colorValue)
+ repository.updateParameter(parameter.id, updatedColorParam)
+ .collect { result ->
+ result.onSuccess {
+ // After color update succeeds, update the printing head parameter
+ val updatedHeadParam = updateParameterContent(
+ printingHeadParam,
+ headNumber.toString()
+ )
+ repository.updateParameter(printingHeadParam.id, updatedHeadParam)
+ .collect { headResult ->
+ headResult.onSuccess {
+ _successMessage.value = "Color updated successfully"
+ loadMachineConfig() // Reload to get the latest data
+ }.onFailure { error ->
+ _errorMessage.value =
+ "Failed to update printing head: ${error.message}"
+ _isLoading.value = false
+ }
+ }
+ }.onFailure { error ->
+ _errorMessage.value = "Failed to update color: ${error.message}"
+ _isLoading.value = false
+ }
+ }
+ } else {
+ // No associated printing head found, just update the color
+ val updatedParam = updateParameterContent(parameter, colorValue)
+ repository.updateParameter(parameter.id, updatedParam)
+ .collect { result ->
+ result.onSuccess {
+ _successMessage.value = "Color updated successfully"
+ loadMachineConfig() // Reload to get the latest data
+ }.onFailure { error ->
+ _errorMessage.value = "Failed to update color: ${error.message}"
+ _isLoading.value = false
+ }
+ }
+ }
+ } catch (e: Exception) {
+ _errorMessage.value = "Error updating color: ${e.message}"
+ _isLoading.value = false
+ }
+ }
+ }
+
+ /**
+ * Find the printing head parameter associated with a color parameter
+ */
+ private fun findPrintingHeadForColorParameter(colorParameter: Parameter): Parameter? {
+ // For coin-color, look for coin-printing-head-no in its parameters
+ if (colorParameter.key == "coin-color") {
+ return colorParameter.parameters["coin-printing-head-no"]
+ }
+
+ // For logo-color, look for printing-head-for-logo-color in its parameters
+ if (colorParameter.key == "logo-color") {
+ return colorParameter.parameters.values.find {
+ it.key.contains("printing-head", ignoreCase = true)
+ }
+ }
+
+ // For nested parameters, check if there's a printing head parameter in the same level
+ colorParameter.parameters.values.forEach { nestedParam ->
+ if (nestedParam.key.contains("printing-head", ignoreCase = true)) {
+ return nestedParam
+ }
+ }
+
+ return null
+ }
