+ modifier: Modifier = Modifier
+) {
+ val minValue = 2.1f
+ val maxValue = 4.0f
+
+ // Parse current value or use default
+ var currentValue by remember {
+ val initial = content.value.toFloatOrNull() ?: minValue
+ mutableStateOf(initial.coerceIn(minValue, maxValue))
+ }
+
+ Column(modifier = modifier.fillMaxWidth()) {
+ Text(
+ text = "Height",
+ style = MaterialTheme.typography.bodyLarge
+ )
+ Spacer(modifier = Modifier.height(8.dp))
+
+ Row(
+ verticalAlignment = Alignment.CenterVertically,
+ modifier = Modifier.fillMaxWidth()
+ ) {
+ Slider(
+ value = currentValue,
+ onValueChange = { value ->
+ currentValue = value
+ // Format to one decimal place
+ val formattedValue = "%.1f".format(value)
+ onValueChange(parameter.id, formattedValue)
+ },
+ valueRange = minValue..maxValue,
+ steps = 19, // (4.0 - 2.1) / 0.1 = 19 steps
+ modifier = Modifier.weight(1f)
+ )
+ Spacer(modifier = Modifier.width(16.dp))
+ Text(
+ text = "%.1f mm".format(currentValue),
+ style = MaterialTheme.typography.bodyMedium,
