+package berlin.tu.snet.coinconfigurator.util

+import android.util.Log
+import berlin.tu.snet.coinconfigurator.model.MachineConfig
+import berlin.tu.snet.coinconfigurator.model.Parameter
+
+/**
+ * Helper class to log parameter IDs during development
+ */
+object ParameterIdHelper {
+ private const val TAG = "ParameterIdHelper"
+
+ /**
+ * Log all parameter IDs and keys in a machine config for debugging
+ */
+ fun logAllParameterIds(config: MachineConfig) {
+ Log.d(TAG, "Logging all parameter IDs for config: ${config.name}")
+
+ val allParameters = flattenParameters(config.parameters)
+ allParameters.forEach { parameter ->
+ Log.d(TAG, "Parameter: ${parameter.key}, ID: ${parameter.id}")
+ }
+
+ Log.d(TAG, "Total parameters: ${allParameters.size}")
+ }
+
+ /**
+ * Log IDs of the specific parameters we're interested in
+ */
+ fun logTargetParameterIds(config: MachineConfig) {
+ Log.d(TAG, "Logging target parameter IDs")
+
+ val targetKeys = listOf(
+ "width",
+ "height",
+ "coin-printing-head-no",
+ "logo",
+ "printing-head-for-logo-color"
+ )
+
+ val allParameters = flattenParameters(config.parameters)
+ targetKeys.forEach { key ->
+ val matchingParameters =
+ allParameters.filter { it.key.contains(key, ignoreCase = true) }
+ if (matchingParameters.isNotEmpty()) {
+ matchingParameters.forEach { param ->
+ Log.d(TAG, "$key - ID: ${param.id}, Full Key: ${param.key}")
+ }
+ } else {
+ Log.d(TAG, "No parameters found with key: $key")
+ }
+ }
+ }
+
+ /**
+ * Check if the config contains all the required parameter IDs
+ */
+ fun verifyRequiredIds(config: MachineConfig): Boolean {
+ val requiredIds = setOf(
+ "d9f2f73b-1ce1-4951-a377-0a6f4ca0b5a3", // Width
+ "378a4533-e4e1-4e5d-96b0-2bf2acb3bdfb", // Height
+ "95ee9a73-2c1e-484c-bf42-a8b22c031966", // Coin Color Printing Head
+ "34867e05-9c0b-4031-b81c-16aed8bbbcd0", // Logo
+ "4902ccfc-d8fb-453e-8ef8-34b06bbdce44" // Logo Color Printing Head
+ )
+
+ val allParameters = flattenParameters(config.parameters)
+ val foundIds = allParameters.map { it.id }.toSet()
+
+ val missingIds = requiredIds - foundIds
+ if (missingIds.isNotEmpty()) {
+ Log.e(TAG, "Missing required parameters: $missingIds")
+ return false
+ }
+
+ return true
+ }
+
+ /**
+ * Convert nested parameter structure to a flat list
+ */
+ private fun flattenParameters(parameters: Map<String, Parameter>): List<Parameter> {
+ val result = mutableListOf<Parameter>()
+
+ parameters.values.forEach { parameter ->
+ result.add(parameter)
+
+ if (parameter.parameters.isNotEmpty()) {
+ result.addAll(flattenParameters(parameter.parameters))
+ }
+ }
+
+ return result
+ }
+}
