+
+/**
+ * Color mapping for printing heads
+ */
+data class PrintingHeadColor(
+ val headNumber: Int,
+ val color: Color,
+ val displayName: String
+)
+
+// Standard colors for the printing heads
+val printingHeadColors = listOf(
+ PrintingHeadColor(1, Color.Red, "Red"),
+ PrintingHeadColor(2, Color.Green, "Green"),
+ PrintingHeadColor(3, Color.Blue, "Blue"),
+ PrintingHeadColor(4, Color.Black, "Black"),
+ PrintingHeadColor(5, Color.White.copy(alpha = 0.9f), "White")
+)
+
+/**
+ * Color selector for printing head parameters (showing colors instead of numbers)
+ */
+@Composable
+fun ColorPrintingHeadSelector(
+ parameter: Parameter,
+ content: ParameterContent,
+ onValueChange: (String, String) -> Unit,
+ modifier: Modifier = Modifier
+) {
+ // Determine proper title based on parameter ID
+ val title = when (parameter.id) {
+ "95ee9a73-2c1e-484c-bf42-a8b22c031966" -> "Coin Color"
+ "4902ccfc-d8fb-453e-8ef8-34b06bbdce44" -> "Logo Color"
+ else -> "Color"
+ }
+
+ // Get the current head number from the parameter value
+ val initialHeadNumber = content.value.toIntOrNull()?.coerceIn(1, 5) ?: 1
+
+ // Find the corresponding color
+ var selectedColor by remember {
+ val initialColor = printingHeadColors.find { it.headNumber == initialHeadNumber }
+ ?: printingHeadColors.first()
+ mutableStateOf(initialColor)
+ }
+
+ Column(modifier = modifier.fillMaxWidth()) {
+ Text(
+ text = title,
+ style = MaterialTheme.typography.bodyLarge
+ )
+
+ Spacer(modifier = Modifier.height(8.dp))
+
+ Row(
+ verticalAlignment = Alignment.CenterVertically,
+ modifier = Modifier.fillMaxWidth()
+ ) {
+ // Show color options
+ printingHeadColors.forEach { colorOption ->
+ ColorOption(
+ colorOption = colorOption,
+ selected = selectedColor.headNumber == colorOption.headNumber,
+ onClick = {
+ selectedColor = colorOption
+ // Update with the head number as the value
+ onValueChange(parameter.id, colorOption.headNumber.toString())
+ }
+ )
+
+ Spacer(modifier = Modifier.width(8.dp))
+ }
+ }
+
+ Spacer(modifier = Modifier.height(4.dp))
+
+ // Show selected color name
+ Text(
+ text = selectedColor.displayName,
+ style = MaterialTheme.typography.bodyMedium,
+ modifier = Modifier.padding(start = 4.dp)
+ )
+ }
+}
+
+/**
+ * Individual color option
+ */
+@Composable
+private fun ColorOption(
+ colorOption: PrintingHeadColor,
+ selected: Boolean,
+ onClick: () -> Unit
+) {
+ val borderWidth = if (selected) 3.dp else 1.dp
+ val borderColor = if (selected) MaterialTheme.colorScheme.primary else Color.Gray
+
+ Box(
+ modifier = Modifier
+ .size(40.dp)
+ .clip(CircleShape)
+ .border(BorderStroke(borderWidth, borderColor), CircleShape)
+ .background(colorOption.color)
+ .clickable(onClick = onClick)
+ )
