/**
 * Screen for displaying and editing the machine configuration
 *
 * @param viewModel ViewModel for handling configuration data
 * @param setSpaceId Function to set the space ID
 * @param setConfigContainerId Function to set the config container ID
 * @param setVersionId Function to set the version ID
 */
@Composable
fun ConfigScreen(
 viewModel: ConfigViewModel,
 setSpaceId: (String) -> Unit,
 setConfigContainerId: (String) -> Unit,
 setVersionId: (String) -> Unit
) {
 val machineConfig by viewModel.machineConfig.collectAsState()
 val isLoading by viewModel.isLoading.collectAsState()
 val errorMessage by viewModel.errorMessage.collectAsState()
 val successMessage by viewModel.successMessage.collectAsState()
 val snackbarHostState = remember { SnackbarHostState() }

 // Show error messages in a snackbar
 LaunchedEffect(errorMessage) {
 errorMessage?.let {
 snackbarHostState.showSnackbar(it)
 viewModel.clearErrorMessage()
 }
 }

 // Show success messages in a snackbar
 LaunchedEffect(successMessage) {
 successMessage?.let {
 snackbarHostState.showSnackbar(it)
 viewModel.clearSuccessMessage()
 }
 }

 // Load the machine configuration when the screen is first shown
 LaunchedEffect(Unit) {
 viewModel.loadMachineConfig()
 }

 Box(modifier = Modifier.fillMaxSize()) {
 if (isLoading) {
 CircularProgressIndicator(modifier = Modifier.align(Alignment.Center))
 } else if (machineConfig != null) {
 ConfigContent(
 machineConfig = machineConfig!!,
 onParameterValueChanged = { parameterId, newValue ->
 viewModel.updateParameterValue(parameterId, newValue)
 }
 )
 } else {
 ConfigSetupContent(
 onLoadConfig = {
 viewModel.loadMachineConfig()
 },
 setSpaceId = setSpaceId,
 setConfigContainerId = setConfigContainerId,
 setVersionId = setVersionId
 )
 }

 SnackbarHost(
 hostState = snackbarHostState,
 modifier = Modifier
 .align(Alignment.BottomCenter)
 .padding(16.dp)
 ) { snackbarData ->
 Snackbar(snackbarData = snackbarData)
 }
 }
}

/**
 * Content shown when machine configuration is loaded
 *
 * @param machineConfig The machine configuration to display
 * @param onParameterValueChanged Callback when a parameter value is changed
 */
@Composable
fun ConfigContent(
