/**
 * Convert nested parameter structure to a flat list
 */
private fun flattenParameters(parameters: Map<String, Parameter>): List<Parameter> {
 val result = mutableListOf<Parameter>()

 // Add all parameters and their nested parameters to the list
 parameters.values.forEach { parameter ->
 // Add the parameter itself
 result.add(parameter)

 // Recursively add nested parameters with proper indentation
 if (parameter.parameters.isNotEmpty()) {
 result.addAll(flattenParameters(parameter.parameters))
 }
 }

 return result
}

/**
 * Group parameters by category for better organization
 *
 * @param parameters The flat list of parameters
 * @return A map of category to list of parameters in that category
 */
private fun groupParametersByCategory(parameters: List<Parameter>): Map<String, List<Parameter>> {
 val result = mutableMapOf<String, MutableList<Parameter>>()

 // Organize parameters based on their IDs for clearer display
 parameters.forEach { parameter ->
 when (parameter.id) {
 "d9f2f73b-1ce1-4951-a377-0a6f4ca0b5a3", // Width
 "378a4533-e4e1-4e5d-96b0-2bf2acb3bdfb" -> { // Height
 val category = "Size"
 result.getOrPut(category) { mutableListOf() }.add(parameter)
 }

 "95ee9a73-2c1e-484c-bf42-a8b22c031966" -> { // Coin Color Printing Head
 val category = "Coin"
 result.getOrPut(category) { mutableListOf() }.add(parameter)
 }

 "34867e05-9c0b-4031-b81c-16aed8bbbcd0" -> { // Logo
 val category = "Logo"
 result.getOrPut(category) { mutableListOf() }.add(parameter)
 }

 "4902ccfc-d8fb-453e-8ef8-34b06bbdce44" -> { // Logo Color Printing Head
 val category = "Logo"
 result.getOrPut(category) { mutableListOf() }.add(parameter)
 }

 else -> {
 val category = "Other"
 result.getOrPut(category) { mutableListOf() }.add(parameter)
 }
 }
 }

 return result
}

