+import androidx.compose.material3.Text
+import androidx.compose.runtime.Composable
+import androidx.compose.runtime.getValue
+import androidx.compose.runtime.mutableStateOf
+import androidx.compose.runtime.remember
+import androidx.compose.runtime.setValue
+import androidx.compose.ui.Alignment
+import androidx.compose.ui.Modifier
+import androidx.compose.ui.text.style.TextAlign
+import androidx.compose.ui.unit.dp
+import berlin.tu.snet.coinconfigurator.model.MachineConfig
+import berlin.tu.snet.coinconfigurator.model.Parameter
+import kotlin.math.roundToInt
+
+/**
+ * Size values for the width slider
+ */
+private val WIDTH_VALUES = listOf(20.00f, 23.25f, 25.75f, 30.00f)
+
+/**
+ * A component that allows users to adjust both width and height of the coin
+ */
+@Composable
+fun CoinSizeSelector(
+ widthParam: Parameter?,
+ heightParam: Parameter?,
+ onWidthChanged: (String, String) -> Unit,
+ onHeightChanged: (String, String) -> Unit,
+ modifier: Modifier = Modifier
+) {
+ Card(
+ modifier = modifier.fillMaxWidth()
+ ) {
+ Column(
+ modifier = Modifier
+ .fillMaxWidth()
+ .padding(16.dp)
+ ) {
+ Text(
+ text = "Coin Size",
+ style = MaterialTheme.typography.titleMedium,
+ modifier = Modifier.padding(bottom = 8.dp)
+ )
+
+ // Width slider
+ if (widthParam != null && widthParam.content.isNotEmpty()) {
+ val currentContent = widthParam.content.first()
+ var selectedIndex by remember {
+ val initialValue = currentContent.value.toFloatOrNull() ?: WIDTH_VALUES[0]
+ val closestIndex = WIDTH_VALUES.indexOfFirst { it == initialValue }
+ mutableStateOf(if (closestIndex >= 0) closestIndex else 0)
+ }
+
+ Spacer(modifier = Modifier.height(16.dp))
+
+ Column {
+ Row(
+ verticalAlignment = Alignment.CenterVertically,
+ modifier = Modifier.fillMaxWidth()
+ ) {
+ Text(
+ text = "Width",
+ style = MaterialTheme.typography.bodyLarge,
+ modifier = Modifier.weight(1f)
+ )
+ Text(
+ text = "%.2f mm".format(WIDTH_VALUES[selectedIndex]),
+ style = MaterialTheme.typography.bodyMedium,
+ textAlign = TextAlign.End,
+ modifier = Modifier.width(80.dp)
+ )
+ }
+
+ Spacer(modifier = Modifier.height(8.dp))
+
+ Slider(
+ value = selectedIndex.toFloat(),
+ onValueChange = { value ->
+ val index = value.roundToInt().coerceIn(0, WIDTH_VALUES.size - 1)
+ if (index != selectedIndex) {
+ selectedIndex = index
+ val newWidth = "%.2f".format(WIDTH_VALUES[index])
+ onWidthChanged(widthParam.id, newWidth)
+ }
+ },
+ valueRange = 0f..(WIDTH_VALUES.size - 1).toFloat(),
+ steps = WIDTH_VALUES.size - 2,
+ modifier = Modifier.fillMaxWidth()
+ )
+
+ Spacer(modifier = Modifier.height(4.dp))
+
+ // Show tick marks with values
+ Row(
+ modifier = Modifier.fillMaxWidth(),
+ verticalAlignment = Alignment.CenterVertically
+ ) {
+ WIDTH_VALUES.forEachIndexed { index, value ->
+ Text(
+ text = "%.0f".format(value),
+ style = MaterialTheme.typography.bodySmall,
+ modifier = Modifier.weight(1f),
+ textAlign = if (index == 0) TextAlign.Start
+ else if (index == WIDTH_VALUES.size - 1) TextAlign.End
+ else TextAlign.Center
+ )
+ }
+ }
+ }
+ }
+
+ // Height slider
+ if (heightParam != null && heightParam.content.isNotEmpty()) {
+ val currentContent = heightParam.content.first()
+ val minHeight = 2.1f
+ val maxHeight = 4.0f
+ var currentHeight by remember {
+ val initialValue = currentContent.value.toFloatOrNull() ?: minHeight
+ mutableStateOf(initialValue.coerceIn(minHeight, maxHeight))
+ }
+
+ Spacer(modifier = Modifier.height(24.dp))
+
+ Column {
+ Row(
+ verticalAlignment = Alignment.CenterVertically,
+ modifier = Modifier.fillMaxWidth()
+ ) {
+ Text(
+ text = "Height",
+ style = MaterialTheme.typography.bodyLarge,
+ modifier = Modifier.weight(1f)
+ )
+ Text(
+ text = "%.1f mm".format(currentHeight),
+ style = MaterialTheme.typography.bodyMedium,
+ textAlign = TextAlign.End,
+ modifier = Modifier.width(80.dp)
+ )
+ }
+
+ Spacer(modifier = Modifier.height(8.dp))
+
+ Slider(
+ value = currentHeight,
+ onValueChange = { value ->
+ val newHeight = value.coerceIn(minHeight, maxHeight)
+ if (newHeight != currentHeight) {
+ currentHeight = newHeight
+ val formattedValue = "%.1f".format(newHeight)
+ onHeightChanged(heightParam.id, formattedValue)
+ }
+ },
+ valueRange = minHeight..maxHeight,
+ steps = 19, // (4.0 - 2.1) / 0.1 = 19 steps
+ modifier = Modifier.fillMaxWidth()
+ )
+
+ Spacer(modifier = Modifier.height(4.dp))
+
+ // Show min and max values
+ Row(
+ modifier = Modifier.fillMaxWidth(),
+ verticalAlignment = Alignment.CenterVertically
+ ) {
+ Text(
+ text = "%.1f".format(minHeight),
+ style = MaterialTheme.typography.bodySmall,
+ textAlign = TextAlign.Start,
+ modifier = Modifier.weight(1f)
+ )
+ Text(
+ text = "%.1f".format(maxHeight),
+ style = MaterialTheme.typography.bodySmall,
+ textAlign = TextAlign.End,
+ modifier = Modifier.weight(1f)
+ )
+ }
+ }
+ }
+ }
+ }
+}
+
+/**
+ * Find parameters by IDs in a MachineConfig
+ *
+ * @param config The machine configuration
+ * @param widthId ID of the width parameter
+ * @param heightId ID of the height parameter
+ * @return Pair of width and height parameters (either can be null if not found)
+ */
+fun findSizeParameters(
+ config: MachineConfig,
+ widthId: String = "d9f2f73b-1ce1-4951-a377-0a6f4ca0b5a3",
+ heightId: String = "378a4533-e4e1-4e5d-96b0-2bf2acb3bdfb"
+): Pair<Parameter?, Parameter?> {
+ val allParameters = flattenParameters(config.parameters)
+ val widthParam = allParameters.find { it.id == widthId }
+ val heightParam = allParameters.find { it.id == heightId }
+ return Pair(widthParam, heightParam)
+}
+
+/**
+ * Flatten a nested parameter structure
+ */
+private fun flattenParameters(parameters: Map<String, Parameter>): List<Parameter> {
+ val result = mutableListOf<Parameter>()
+
+ parameters.values.forEach { parameter ->
+ result.add(parameter)
+
+ if (parameter.parameters.isNotEmpty()) {
+ result.addAll(flattenParameters(parameter.parameters))
+ }
+ }
+
+ return result
+}
```

