@Composable
fun PreferencesDialog(
 viewModel: ConfigViewModel,
 onDismissRequest: () -> Unit = {}
) {
 val preferences = viewModel.configPreferences
 val scope = rememberCoroutineScope()

 // Create a single state object for all color values
 val colorState = remember { 
 mutableStateOf(
 mapOf(
 1 to "",
 2 to "",
 3 to "",
 4 to "",
 5 to ""
 )
 ) 
 }

 // State for validation errors
 val errorState = remember { mutableStateMapOf<Int, String>() }

 // Load current colors
 LaunchedEffect(key1 = true) {
 val colors = viewModel.colorPrintHeadMap.value
 val newColorMap = mapOf(
 1 to String.format("%06X", 0xFFFFFF and (colors[1]?.toInt() ?: 0xFF0000)),
 2 to String.format("%06X", 0xFFFFFF and (colors[2]?.toInt() ?: 0x00FF00)),
 3 to String.format("%06X", 0xFFFFFF and (colors[3]?.toInt() ?: 0x0000FF)),
 4 to String.format("%06X", 0xFFFFFF and (colors[4]?.toInt() ?: 0xFFFF00)),
 5 to String.format("%06X", 0xFFFFFF and (colors[5]?.toInt() ?: 0x00FFFF))
 )
 colorState.value = newColorMap
 }

 AlertDialog(
 onDismissRequest = onDismissRequest,
 title = { Text("Printing Head Colors") },
 text = {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .verticalScroll(rememberScrollState())
 ) {
 Text(
 "Define colors for each printing head",
 style = MaterialTheme.typography.bodyMedium
 )

 Spacer(modifier = Modifier.height(16.dp))

 for (headNumber in 1..5) {
 ColorInputRow(
 headNumber = headNumber,
 colorValue = colorState.value[headNumber] ?: "",
 onColorChange = { newColor ->
 val updatedColors = colorState.value.toMutableMap()
 updatedColors[headNumber] = newColor
 colorState.value = updatedColors
 },
 errorMessage = errorState[headNumber],
 modifier = Modifier.fillMaxWidth()
 )

 Spacer(modifier = Modifier.height(8.dp))
 }
 }
 },
 confirmButton = {
 Button(
 onClick = {
 // Clear previous errors
 errorState.clear()

 // Validate and save colors
 var allValid = true

 // Validate all colors
 colorState.value.forEach { (headNumber, hexValue) ->
 if (hexValue.length != 6) {
 errorState[headNumber] = "Color must be 6 hex digits"
 allValid = false
 } else {
 try {
 hexValue.toLong(16)
 } catch (e: Exception) {
 errorState[headNumber] = "Invalid color format"
 allValid = false
 }
 }
 }

 // If all colors are valid, save them
 if (allValid) {
 colorState.value.forEach { (headNumber, hexValue) ->
 val colorLong = hexValue.toLong(16) or 0xFF000000
 // Save to preferences
 scope.launch {
 preferences.setPrintingHeadColor(colorLong, headNumber)
 }
 // Update UI immediately
 viewModel.updatePrintHeadColor(headNumber, colorLong)
 }
 onDismissRequest()
 }
 }
 ) {
 Text("Save")
 }
 },
 dismissButton = {
 TextButton(onClick = onDismissRequest) {
 Text("Cancel")
 }
 }
 )
}

@Composable
private fun ColorInputRow(
 headNumber: Int,
 colorValue: String,
 onColorChange: (String) -> Unit,
 errorMessage: String?,
 modifier: Modifier = Modifier
) {
 Row(
 modifier = modifier,
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 // Color preview
 val color = try {
 Color(android.graphics.Color.parseColor("#$colorValue"))
 } catch (e: Exception) {
 Color.Gray
 }

 Box(
 modifier = Modifier
 .size(40.dp)
 .background(color)
 .border(1.dp, Color.Black, RoundedCornerShape(4.dp))
 )

 Column(modifier = Modifier.weight(1f)) {
 OutlinedTextField(
 value = colorValue,
 onValueChange = { value ->
 // Accept only hex characters and limit to 6 characters
 if (value.matches(Regex("^[0-9A-Fa-f]{0,6}$"))) {
 onColorChange(value.uppercase())
 }
 },
 label = { Text("Printing Head $headNumber Color (Hex)") },
 prefix = { Text("#") },
 singleLine = true,
 isError = errorMessage != null,
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Text,
 imeAction = ImeAction.Next
 ),
 modifier = Modifier.fillMaxWidth()
 )

 if (errorMessage != null) {
 Text(
 text = errorMessage,
 color = MaterialTheme.colorScheme.error,
 style = MaterialTheme.typography.bodySmall,
 modifier = Modifier.padding(start = 4.dp, top = 4.dp)
 )
 }
 }
 }
