+package berlin.tu.snet.coinconfigurator.viewmodel

+import androidx.lifecycle.ViewModel
+import androidx.lifecycle.viewModelScope
+import berlin.tu.snet.coinconfigurator.data.ConfigPreferences
+import kotlinx.coroutines.flow.MutableStateFlow
+import kotlinx.coroutines.flow.StateFlow
+import kotlinx.coroutines.flow.asStateFlow
+import kotlinx.coroutines.flow.SharingStarted
+import kotlinx.coroutines.flow.stateIn
+import kotlinx.coroutines.flow.map
+import kotlinx.coroutines.launch
+import kotlinx.coroutines.flow.catch
+
+class SettingsViewModel(
+ private val configPreferences: ConfigPreferences
+) : ViewModel() {
+
+ // State for the UI
+ private val _settingsState = MutableStateFlow(SettingsState())
+ val settingsState: StateFlow<SettingsState> = _settingsState.asStateFlow()
+
+ // Success/error messages
+ private val _message = MutableStateFlow<String?>(null)
+ val message: StateFlow<String?> = _message.asStateFlow()
+
+ // Track loading state
+ private val _isLoading = MutableStateFlow(false)
+ val isLoading: StateFlow<Boolean> = _isLoading.asStateFlow()
+
+ // Get the color mapping for all printing heads
+ val colorMappings = MutableStateFlow<Map<Int, Long>>(emptyMap())
+
+ init {
+ loadSettings()
+ }
+
+ private fun loadSettings() {
+ viewModelScope.launch {
+ _isLoading.value = true
+
+ try {
+ // Collect values from preferences
+ configPreferences.baseUrl.collect { baseUrl ->
+ _settingsState.value = _settingsState.value.copy(baseUrl = baseUrl)
+ }
+ } catch (e: Exception) {
+ _message.value = "Error loading settings: ${e.message}"
+ }
+
+ try {
+ configPreferences.spaceId.collect { spaceId ->
+ _settingsState.value = _settingsState.value.copy(spaceId = spaceId)
+ }
+ } catch (e: Exception) {
+ _message.value = "Error loading space ID: ${e.message}"
+ }
+
+ try {
+ configPreferences.configContainerId.collect { configContainerId ->
+ _settingsState.value =
+ _settingsState.value.copy(configContainerId = configContainerId)
+ }
+ } catch (e: Exception) {
+ _message.value = "Error loading container ID: ${e.message}"
+ }
+
+ try {
+ configPreferences.versionId.collect { versionId ->
+ _settingsState.value = _settingsState.value.copy(versionId = versionId)
+ }
+ } catch (e: Exception) {
+ _message.value = "Error loading version ID: ${e.message}"
+ }
+
+ // Load color mappings
+ loadColorMappings()
+
+ _isLoading.value = false
+ }
+ }
+
+ private suspend fun loadColorMappings() {
+ val mappings = mutableMapOf<Int, Long>()
+ for (i in 1..5) {
+ mappings[i] = configPreferences.getColorForPrintingHead(i)
+ }
+ colorMappings.value = mappings
+ }
+
+ fun updateSpaceId(newValue: String) {
+ viewModelScope.launch {
+ _isLoading.value = true
+ try {
+ configPreferences.setSpaceId(newValue)
+ _settingsState.value = _settingsState.value.copy(spaceId = newValue)
+ _message.value = "Space ID updated"
+ } catch (e: Exception) {
+ _message.value = "Error updating space ID: ${e.message}"
+ }
+ _isLoading.value = false
+ }
+ }
+
+ fun updateConfigContainerId(newValue: String) {
+ viewModelScope.launch {
+ _isLoading.value = true
+ try {
+ configPreferences.setConfigContainerId(newValue)
+ _settingsState.value = _settingsState.value.copy(configContainerId = newValue)
+ _message.value = "Config container ID updated"
+ } catch (e: Exception) {
+ _message.value = "Error updating config container ID: ${e.message}"
+ }
+ _isLoading.value = false
+ }
+ }
+
+ fun updateVersionId(newValue: String) {
+ viewModelScope.launch {
+ _isLoading.value = true
+ try {
+ configPreferences.setVersionId(newValue)
+ _settingsState.value = _settingsState.value.copy(versionId = newValue)
+ _message.value = "Version ID updated"
+ } catch (e: Exception) {
+ _message.value = "Error updating version ID: ${e.message}"
+ }
+ _isLoading.value = false
+ }
+ }
+
+ fun updatePrintingHeadColor(headNumber: Int, color: Long) {
+ viewModelScope.launch {
+ _isLoading.value = true
+ try {
+ configPreferences.setPrintingHeadColor(color, headNumber)
+ val updatedMappings = colorMappings.value.toMutableMap()
+ updatedMappings[headNumber] = color
+ colorMappings.value = updatedMappings
+ _message.value = "Printing head $headNumber color updated"
+ } catch (e: Exception) {
+ _message.value = "Error updating printing head color: ${e.message}"
+ }
+ _isLoading.value = false
+ }
+ }
+
+ fun resetColorMappings() {
+ viewModelScope.launch {
+ _isLoading.value = true
+ try {
+ configPreferences.initializeDefaultColorMappings()
+ loadColorMappings()
+ _message.value = "Color mappings reset to defaults"
+ } catch (e: Exception) {
+ _message.value = "Error resetting color mappings: ${e.message}"
+ }
+ _isLoading.value = false
+ }
+ }
+
+ fun clearMessage() {
+ _message.value = null
+ }
+}
+
+data class SettingsState(
+ val baseUrl: String = "",
+ val spaceId: String = "",
+ val configContainerId: String = "",
+ val versionId: String = ""
+)
